mapper.delete:
delete from setmeal
where id in

    #{id}

mapper.select:
select *
from setmeal


        category_id = #{categoryId}


        and name = #{name}


        and status = #{status}


order by create_time desc

select * from setmeal
where id in

    #{id}

select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}
