mapper.delete:
delete from shopping_cart


        user_id = #{userId}


        and setmeal_id = #{setmealId}


        and dish_id = #{dishId}


        and dish_flavor =  #{dishFlavor}

mapper.select:
select * from shopping_cart


        user_id = #{userId}


        and setmeal_id = #{setmealId}


        and dish_id = #{dishId}


        and dish_flavor =  #{dishFlavor}


order by create_time desc
